import React from "react";
import { Box, Container, Link } from "@mui/material";
import { Link as RouterLink } from "react-router-dom";

const HomePage = () => {
  const bgImage =
    "https://kartinkin.net/pics/uploads/posts/2022-08/1660339458_4-kartinkin-net-p-alma-ata-stolitsa-krasivo-foto-4.jpg";
  return (
    <Box
      sx={{
        backgroundImage: `url(${bgImage})`,
        height: "100vh",
        backgroundSize: "cover",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        flexDirection: "column",
      }}
    >
      <Link
        component={RouterLink}
        to="/"
        variant="h2"
        color="common.white"
        gutterBottom
      >
        Добро пожаловать в Казахстан
      </Link>
      <Container>
        <Box display="flex" justifyContent="space-between">
          <Link component={RouterLink} to="/" variant="h6" color="common.white">
            Главное
          </Link>
          <Link
            component={RouterLink}
            to="/top-places"
            variant="h6"
            color="common.white"
          >
            Топ мест
          </Link>
          <Link
            component={RouterLink}
            to="/useful-info"
            variant="h6"
            color="common.white"
          >
            Полезное
          </Link>
          <Link
            component={RouterLink}
            to="/about"
            variant="h6"
            color="common.white"
          >
            О нас
          </Link>
        </Box>
      </Container>
    </Box>
  );
};

export default HomePage;
