import React from "react";
import { Box, Link } from "@mui/material";
import { Link as RouterLink } from "react-router-dom";
import { BrowserRouter as Router } from "react-router-dom";

const HomePage = () => {
  const bgImage =
    "https://kartinkin.net/pics/uploads/posts/2022-08/1660339458_4-kartinkin-net-p-alma-ata-stolitsa-krasivo-foto-4.jpg";
  return (
    <Router>
      <Box
        sx={{
          backgroundImage: `url(${bgImage})`,
          height: "100vh",
          backgroundSize: "cover",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <Link
          component={RouterLink}
          to="/"
          variant="h2"
          color="common.white"
          gutterBottom
        >
          Добро пожаловать в Казахстан
        </Link>
        <Box
          sx={{
            position: "absolute",
            top: "20px",
            left: "20px",
            display: "flex",
            flexDirection: "column",
          }}
        >
          <Link
            component={RouterLink}
            to="/"
            variant="h6"
            color="common.white"
            sx={{ marginBottom: "20px" }}
          >
            Главное
          </Link>
          <Link
            component={RouterLink}
            to="/top-places"
            variant="h6"
            color="common.white"
            sx={{ marginBottom: "20px" }}
          >
            Топ мест
          </Link>
          <Link
            component={RouterLink}
            to="/useful-info"
            variant="h6"
            color="common.white"
            sx={{ marginBottom: "20px" }}
          >
            Полезное
          </Link>
          <Link
            component={RouterLink}
            to="/about"
            variant="h6"
            color="common.white"
            sx={{ marginBottom: "20px" }}
          >
            О нас
          </Link>
        </Box>
      </Box>
    </Router>
  );
};

export default HomePage;
