// Определение маршрутов для каждой страницы
const routes = {
  "/": "home.html",
  "/about": "about.html",
  "/beautifulplaces": "beautifulplaces.html",
  "/OpenAi": "OpenAi.html"
};

// Функция для загрузки содержимого страницы
const loadPage = () => {
  // Получение текущего маршрута
  const pathName = window.location.pathname;

  // Определение, какая страница должна быть загружена
  const page = routes[pathName];

  // Загрузка страницы по указанному пути
  fetch(page)
    .then(response => response.text())
    .then(html => {
      // Вставка содержимого страницы в тег body
      document.querySelector("body").innerHTML = html;
    })
    .catch(error => console.error(error));
};

// Загрузка страницы по умолчанию
loadPage();
